mapper.select:
select s1.ID,s1.TYPENAME,s1.CLEVEL,s1.FID,s1.UNIT,s1.ISPRODUCE,decode(s1.ISPRODUCE,'0','否','10','是','否') as isproducename,s2.typename as ftypename from T_COST_SPLTYPEINFO s1 left outer join T_COST_SPLTYPEINFO s2 on s1.fid=s2.id where s1.DELSTATUS = '10'
AND s1.FID = #{fid}
order by id desc

		select ID, TYPENAME, CLEVEL, FID, UNIT, PRICE, DELSTATUS, ISPRODUCE
		from T_COST_SPLTYPEINFO s
		where s.DELSTATUS = '10' 

        AND s.FID = #{fid} 


        and s.CLEVEL= #{clevel}


        and s.TYPENAME like '%'||#{typename}||'%' 


        and s.isproduce=#{isproduce}

	order by id desc
