mapper.select:
select INBOUNDID, NAVICERT, CONTRACTNO, CREATETIME, QTY, SUPPLIER,b.comapany as CONSIGNSOR, INTYPE, REMARKS, STATUS,decode(INTYPE,'10','工业来烟','20','调拨入库','30','退货入库','40','罚没烟') as INTYPENAME, decode(STATUS,'10','新增','20','入库中','30','入库完成','新增') as STATUSNAME from T_WMS_INBOUND a,t_wms_consignor b where a.consignsor=b.code
AND STATUS=#{param.status}
AND intype = #{param.intype}
AND (b.comapany like '%'||#{param.keyword}||'%' or NAVICERT like '%'||#{param.keyword}||'%' or CONTRACTNO like '%'||#{param.keyword}||'%')
ORDER BY INBOUNDID DESC
